mapper.select:


	SELECT *
FROM
  (
  SELECT *
  FROM
  (
    SELECT ROWNUM AS SEQ,INQUIRY_NO,MEMBER_NO,PRODUCT_NO,INQUIRY_TITLE,INQUIRY_CONTENT,INQUIRY_WRT_DATE,INQUIRY_MOD_DATE,INQUIRY_DEL_DATE 
    FROM
      (
        SELECT *
        FROM INQUIRY
        WHERE PRODUCT_NO = #{productNo}
        ORDER BY INQUIRY_NO
      )
  )
WHERE SEQ >= #{startIndex}
)
WHERE   ROWNUM <= #{pageSize}
